import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './Items.css';

const Items = () => {
    const [item, setItem] = useState({});

    useEffect(() => {
        axios.get('http://localhost:8080/items/1')
        .then(resp => {
            console.log(resp);
            setItem(resp.data);
        })
        .catch(err => console.log(err))

    }, []);

    let email = `mailto:${item.email}`;
    return (
        <div id="item-wrapper">
            <h1 id="title">{item.name}</h1>
            <h1 className="details">{item.price}$</h1>
            <h1 className="details">{item.number}</h1>
            <div id="desc">
                <p>{item.description}</p>
            </div>
            <img id="pic" src={`data:image/jpeg;base64,${item.picture}`} />
            <button class="buy-btn">Buy</button>
            <button class="buy-btn"><a href={email}>Message</a></button>
        </div>
    )
}


export default Items;
