import React, { useEffect, useState } from "react";
import './Nav.css';
import {Link} from 'react-router-dom';

let Nav = () => {
    window.renderLog = () => {
        if(localStorage.getItem('user')){
            return <Link to="/">Logout</Link>
        }else {
            return <Link to="/login">Login</Link>
        };
    }

    const authRender = () => {
        if(localStorage.getItem('user')){
            return <Link to="/">Logout</Link>
        }else {
            return <Link to="/login">Login</Link>
        }
    }

    let [text, setText] = useState(authRender());

    const authHandler = () => {
        if(localStorage.getItem('user')){
            localStorage.removeItem('user');
            setText
        }
    }

    useEffect(() => {
        setText(authRender());
    }, localStorage.getItem('user'));

    return (
        <div className="nav">
            <button onClick={authHandler} className="shopping-cart">{window.renderLog()}</button>
            <button className="btn"><Link to="/">Home</Link></button>
            <button className="btn"><Link to="/about">About</Link></button>
            <button className="btn"><Link to="/items">Items</Link></button>
        </div>
    );
}

export default Nav;
