import React, { useState } from "react";
import './Nav.css';
import {Link} from 'react-router-dom';

let Nav = () => {
    const authRenderer = () => {
        if(localStorage.getItem('user')){
            return <Link to="/">Logout</Link>
        }else {
            return <Link to="/login">Login</Link>
        }
    }

    let [text, setText] = useState(authRenderer());

    const authHandler = () => {
        if(localStorage.getItem('user')){
            localStorage.removeItem('user');
        }else{
            localStorage.setItem('user', {
                name: 'user'
            })
        }
        setText(authRenderer());
    }

    return (
        <div className="nav">
            <button onClick={authHandler} className="shopping-cart">{authRenderer()}</button>
            <button className="btn"><Link to="/">Home</Link></button>
            <button className="btn"><Link to="/about">About</Link></button>
            <button className="btn"><Link to="/items">Items</Link></button>
        </div>
    );
}

export default Nav;
