import React, { useEffect, useState } from "react";
import './Nav.css';
import {Link} from 'react-router-dom';

let Nav = () => {
    const [component, setComponent] = useState(localStorage.getItem('user') ?<Link to="/">Logout</Link>  :<Link to="/login">Login</Link>)

    if(localStorage.getItem('user')){
        setComponent(<Link to="/">Logout</Link>);
    }
    return (
        <div className="nav">
            <button className="shopping-cart">{component}</button>
            <button className="btn"><Link to="/">Home</Link></button>
            <button className="btn"><Link to="/about">About</Link></button>
            <button className="btn"><Link to="/items">Items</Link></button>
        </div>
    );
}

export default Nav;
