import React from 'react';
import axios from 'axios';
import './AddPost.css';

let submitHandler = (e) => {
    e.preventDefault();
    let firstDataWrapper = document.getElementById('title-price');
    let secondDataWrapper = document.getElementById('desc-photo');

    let itemName = firstDataWrapper.firstElementChild.value;
    let itemPrice = firstDataWrapper.lastElementChild.value;
    let picture = secondDataWrapper.lastElementChild.files[0];
    let itemDesc = secondDataWrapper.firstElementChild.nextElementSibling.value;

    let form = new FormData();
    form.append('itemName', itemName);
    form.append('itemDesc', itemDesc);
    form.append('itemPrice', itemPrice);
    form.append('picture', picture);
    axios.post('http://localhost:8080/add', null, {
        params: {
            itemName,
            itemDesc,
            itemPrice,
            picture
        }
    }).then(res => console.log(res))
    .catch(err => console.log(err));
}

const AddPost = () => {
    return (
        <div id="form-wrapper">
            <form onSubmit={submitHandler} id="add-form">
                <div id="desc-photo">
                    <h2>Add description</h2>
                    <textarea placeholder="Add your description here"></textarea>
                    <input type="file" name="file"/>
                </div>
                <div id="title-price">
                    <input name="title"></input>
                    <input type="number" name="price"/>
                </div>
                <input type="submit"/>
            </form>
        </div>
    );
}

export default AddPost;
