import React from 'react';
import axios from 'axios';
import './AddPost.css';

let submitHandler = (e) => {
    e.preventDefault();
    let firstDataWrapper = document.getElementById('title-price');
    let secondDataWrapper = document.getElementById('desc-photo');
    let thirdDataWrapper = document.getElementById('contacts');

    let itemName = firstDataWrapper.firstElementChild.value;
    let itemPrice = firstDataWrapper.lastElementChild.value;
    let picture = secondDataWrapper.lastElementChild.files[0];
    let itemDesc = secondDataWrapper.firstElementChild.nextElementSibling.value;
    let itemNumber = thirdDataWrapper.lastElementChild.value;
    let email = thirdDataWrapper.firstElementChild.nextElementSibling.value;
    let password = thirdDataWrapper.lastElementChild.previousElementSibling.previousElementSibling.value;

    let form = new FormData();
    form.append('itemName', itemName);
    form.append('itemDesc', itemDesc);
    form.append('itemPrice', itemPrice);
    form.append('picture', picture);
    form.append('itemNumber', itemNumber);
    form.append('email', email);
    form.append('password', password);

    axios.post('http://localhost:8080/add', form)
    .then(res => console.log(res))
    .catch(err => console.log(err));
}

const AddPost = () => {
    return (
        <div id="item-wrapper">
            <form onSubmit={submitHandler} id="add-form">
                    <div id="photo">
                        <input type="file" style={{display: 'none'}} name="file"/><br/>
                    </div>
                    <label className="text-fields" htmlFor="title">Title</label><br/>
                    <input name="title" className="text-fields" type="text"></input><br/>
                    <label htmlFor="price" className="text-fields">Price</label><br/>
                    <input type="number" className="text-fields" name="price"/><br/>
                    <label htmlFor="email" className="text-fields">Email</label><br/>
                    <input name="email" type="text" className="text-fields"/><br/>
                    <label htmlFor="password" className="text-fields">Password</label><br/>
                    <input name="password" className="text-fields" type="password"/><br/>
                    <label htmlFor="number" className="text-fields">Number</label><br/>
                    <input name="number" className="text-fields" type="text"/><br/>
                    <div id="desc-item">
                        <label htmlFor="desc">Add description</label><br/>
                        <input name="desc" type="text" placeholder="Add your description here" /><br/>
                    </div>
                    <input type="submit"/>
            </form>
        </div>
    );
}

export default AddPost;
